changeSet.sql:
-- Insert emails INSERT INTO EMAIL_DATA (user_id, email) SELECT id, LOWER(name) || '-' || id || '@example.com' FROM "USER";
-- Insert phone numbers (format: 79XXXXXXXXX) INSERT INTO PHONE_DATA (user_id, phone) SELECT id, '79' || FLOOR(RANDOM() * 1000000000)::TEXT -- random generates from 0 to 1, i.e. min case will be 790 FROM "USER";
-- Insert accounts with random balance between 0 and 1000 INSERT INTO ACCOUNT (user_id, balance) SELECT id, ROUND((RANDOM() * 1000)::numeric, 2) -- convert to ,XX precision FROM "USER";
